A= (6, 8);
B=(18, 8);
C=(24, 0);
D=(0,0);
O=(12,0);
P=(12,8);
X=(A+D)/2;
Y=(B+C)/2;
draw(X--Y, dashed);
draw(A--B--C--D--A);
draw(A--O--B);
draw(O--P, dashed);
label("A", A, NW);
label("B", B, NE);
label("C", C, SE);
label("D", D, SW);
label("O", O, S);
label("P", P, N);
label("X", X, NW);
label("Y", Y, NE);
